'use strict';

function parseFile(baseDir, file) {
  var text = require("fs").readFileSync(require("path").join(baseDir, file.relPath), 'utf8');

  return require("graphql").parse(new (require("graphql").Source)(text, file.relPath), {
    experimentalFragmentVariables: true
  });
}

function getParser(baseDir) {
  return new (require("./ASTCache"))({
    baseDir: baseDir,
    parse: parseFile
  });
}

module.exports = {
  parseFile: parseFile,
  getParser: getParser
};